import {
  StyleSheet,
  StatusBar,
  Text,
  View,
  Image,
  TouchableOpacity,
  ScrollView,
} from "react-native";
import React from "react";
import globalStyles from "../../styles/styles";

{
  /* COMPONENTS */
}
import { MenuComponent } from "../../components";

const ProductScreen = ({ route }) => {
  const { title, price, expirationDate, url, seller } = route.params.product;

  return (
    <View style={{ flex: 1 }}>
      <View style={styles.viewContainer}>
        <StatusBar backgroundColor="white" barStyle="dark-content" />

        {/* MENU */}
        <MenuComponent />

        <ScrollView
          showsVerticalScrollIndicator={false}
          style={{ paddingHorizontal: 15 }}
        >
          <Text
            style={[
              styles.productTitle,
              globalStyles.fontBold,
              globalStyles.fontMain,
            ]}
          >
            {title}
          </Text>

          <View
            style={[
              styles.productBanner,
              styles.bannerLargeMargin,
              globalStyles.row,
              globalStyles.fontSecondary,
            ]}
          >
            <View style={styles.bannetTitles}>
              <Text
                style={[
                  styles.bannerSamllMargin,
                  globalStyles.fontMedium,
                  globalStyles.fontBold,
                ]}
              >
                Precio
              </Text>
              <Text style={[styles.bannerLargeMargin, globalStyles.fontMedium]}>
                $ {price}
              </Text>

              <Text
                style={[
                  styles.bannerSamllMargin,
                  globalStyles.fontMedium,
                  globalStyles.fontBold,
                ]}
              >
                Fecha de Vencimmineto
              </Text>
              <Text style={[styles.bannerSamllMargin, globalStyles.fontMedium]}>
                {expirationDate}
              </Text>
            </View>

            <Image style={styles.productImage} source={{ uri: url }} />
          </View>

          <View style={globalStyles.row}>
            <View style={globalStyles.widthHalf}>
              <Text
                style={[
                  styles.bannerSamllMargin,
                  globalStyles.fontMedium,
                  globalStyles.fontBold,
                ]}
              >
                Comercio
              </Text>

              <View
                style={[
                  styles.seller,
                  globalStyles.row,
                  globalStyles.alignItemsCenter,
                  globalStyles.fontSmall,
                ]}
              >
                <Image
                  style={styles.productSeller}
                  source={{ uri: seller.url }}
                />

                <Text>{seller.title}</Text>
              </View>
            </View>

            <View style={globalStyles.widthHalf}>
              <Text
                style={[
                  styles.bannerSamllMargin,
                  globalStyles.fontMedium,
                  globalStyles.fontBold,
                ]}
              >
                Cantidad
              </Text>

              <View
                style={[
                  styles.bannerLargeMargin,
                  globalStyles.row,
                  globalStyles.alignItemsCenter,
                ]}
              >
                <Text style={globalStyles.sellIcons}>-</Text>
                <Text style={{ marginHorizontal: 15 }}>1</Text>
                <Text style={globalStyles.sellIcons}>+</Text>
              </View>
            </View>
          </View>

          <Text
            style={[
              styles.bannerSamllMargin,
              globalStyles.fontMedium,
              globalStyles.fontBold,
            ]}
          >
            Descripción
          </Text>

          <Text style={[globalStyles.fontSmall]}>
            Es un tipo de pan que se diferencia del francés o común porque se
            elabora con leche y materia grasa y se hornea en moldes de forma
            alargada y rectangular. Además, se caracteriza por tener una textura
            blanda.
          </Text>

          <View style={{ height: 180 }}></View>
        </ScrollView>
      </View>

      {/* BUTTON */}
      <TouchableOpacity
        onPress={() => navigator.navigate("Home")}
        style={[
          styles.purchaseButton,
          globalStyles.widthFluid,
          globalStyles.primary,
        ]}
      >
        <Text style={globalStyles.textWhite}>Agregar al carrito</Text>
      </TouchableOpacity>
    </View>
  );
};

export default ProductScreen;

const styles = StyleSheet.create({
  viewContainer: {
    // paddingTop: 40,
    paddingHorizontal: 10,
  },

  seller: {
    width: "50%",
  },

  productTitle: {
    fontSize: 38,
    width: "50%",
  },

  productSeller: {
    width: 40,
    height: 40,
  },

  productBanner: {
    alignItems: "flex-end",
  },

  bannetTitles: {
    paddingBottom: 20,
    width: "50%",
  },

  bannerSamllMargin: {
    marginBottom: 10,
  },

  bannerLargeMargin: {
    marginBottom: 25,
  },

  productImage: {
    width: 250,
    height: 250,
    borderRadius: 300,
    right: -20,
    backgroundColor: "#F4F4F4",
  },

  purchaseButton: {
    position: "absolute",
    bottom: 0,
    padding: 15,
    borderTopStartRadius: 20,
    borderTopEndRadius: 20,
    justifyContent: "center",
    alignItems: "center",
  },
});
