import { StyleSheet } from 'react-native';

export default StyleSheet.create({
  
    primary: {
        backgroundColor: '#6CAA7A'
    },
  
    secondary: {
        backgroundColor: '#4DB591'
    },

    scrollContainer: {
        justifyContent: 'center',
        alignItems: 'center',
    },

    arrowContainer: {
        width: '85%',
        marginTop: 40
    },

    backArrow: {
        backgroundColor: '#C4C4C4',
        width: 28,
        borderRadius: 100,
    },

    arrow: {
        color: '#3D9D5D',
        paddingVertical: 5,
        marginLeft: 7,
        fontSize: 20,
        textAlign: 'center'
    },

    fontLarge: {
        fontSize: 24,
    },

    fontMedium: {
        fontSize: 16,
    },

    fontSmall: {
        fontSize: 14,
    },

    fontXSmall: {
        fontSize: 12,
    },

    fontBold: {
        fontWeight: 'bold'
    },

    fontMain: {
        // fontFamily: 'Poppins'
    },

    fontSecondary: {
        fontFamily: 'Karla'
    },

    textCenter: {
        textAlign: 'center'
    },

    widthFluid: {
        width: '100%'
    },

    widthHalf: {
        width: '50%'
    },

    widthEightyFive: {
        width: '85%'
    },

    row: {
        flexDirection: 'row',
    },

    justifyContentCenter: {
        justifyContent: 'center'
    },

    justifyContentBetween: {
        justifyContent: 'space-between'
    },

    justifyContentAround: {
        justifyContent: 'space-around'
    },

    alignItemsStart: {
        alignItems: 'flex-start'
    },

    alignItemsCenter: {
        alignItems: 'center'
    },

    inputLabel: {
        color: '#888888',
        marginBottom: 8
    },

    inputRow: {
        flexDirection: 'row',
        backgroundColor: '#F9FAFB',
        borderRadius: 3,
        marginBottom: 25,
        alignItems: 'center'
    },

    icons: {
        color: '#979797',
        fontSize: 24,
        marginStart: 10
    },

    input: {
        paddingHorizontal: 15,
        paddingVertical: 15,
        width: '100%'
    },

    button: {
        padding: 15,
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center'
    },

    textWhite: {
        color: 'white'
    },

    menuLogo: {
        width: 100,
        height: 60
    },

    sellIcons: {
        borderRadius: 5,
        borderWidth: 1,
        paddingHorizontal: 6,
        paddingVertical: 3
    },

    container: {
        flex: 1,
        justifyContent: "center"
    },

    horizontal: {
        flexDirection: "row",
        justifyContent: "space-around",
    },

    //..............

    shadow: {
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 2
        },
        shadowOpacity: 0.25,
        shadowRadius: 4,
        elevation: 5
    },

});
